import React, { Component } from 'react';
import './ExploreContainer.css';
import Wave from 'react-wavify';

export default class ExploreContainer extends Component {

	constructor(props: any){
	 super(props);
	 this.state = { time: {}, seconds: 5 };
	}

	render() {

	return(
	<div className="container">

       <div className="audio_div">
       <strong>Breathe IN</strong>
       	<Wave fill="#e62315" mask="url(#mask)" options={{ points: 20, speed: 0.2, amplitude: 40 }}>
 			<mask id="mask">
    			<path d="M10,35 A20,20,0,0,1,50,35 A20,20,0,0,1,90,35 Q90,65,50,95 Q10,65,10,35 Z" fill="white" />
 			</mask>
		</Wave>
       <br/>
        <audio ref="audio_tag" src={"/assets/sounds/audio_file.mpeg"} controls autoPlay loop />
      	</div>
    </div>
	);

	}
}
